 select detailjual.kode_brng,databarang.nama_brng, detailjual.kode_sat, kodesatuan.satuan,jenis.nama as namajenis,sum(detailjual.jumlah) as jumlah,sum(detailjual.total) as total, i.nama_industri from penjualan inner join petugas inner join bangsal inner join jenis   inner join detailjual inner join databarang inner join kodesatuan inner join industrifarmasi i on detailjual.kode_brng=databarang.kode_brng  and databarang.kode_sat=kodesatuan.kode_sat  and penjualan.kd_bangsal=bangsal.kd_bangsal  and penjualan.nota_jual=detailjual.nota_jual  and penjualan.nip=petugas.nip and databarang.kdjns=jenis.kdjns and databarang.kode_industri=i.kode_industri where penjualan.status='Sudah Dibayar' and penjualan.tgl_jual BETWEEN '2023-03-01' and '2023-03-16'  group by detailjual.kode_brng    select * from penjualan p; select * from industrifarmasi i;  and  and (penjualan.nota_jual like '%%' or penjualan.no_rkm_medis like '%%' or penjualan.nm_pasien like '%%' or  penjualan.nip like '%%' or petugas.nama like '%%' or penjualan.keterangan like '%%' or penjualan.jns_jual like '%%' or detailjual.kode_brng like '%%' or bangsal.nm_bangsal like '%%' or databarang.nama_brng like '%%' or databarang.kode_sat like '%%' or jenis.nama like '%%')  group by detailjual.kode_brng 